sys_script_client.script:
function onChange(control, storedValue, newValue, isLoading, isTemplate) {
	var fieldName = 'u_what_needed';
	var options = [];

	// start with no options until a value is set
	g_form.clearOptions(fieldName);

	// set options of 'WhatNeeded' field according to value of newValue
	switch(newValue) {
		case 'hr':
			options.push({
				choiceValue: 'hr1',
				choiceLabel: 'Human Resources 1'
			});

			options.push({
				choiceValue: 'hr2',
				choiceLabel: 'Human Resources 2'
			});
			break;
		case 'facilities':
			options.push({
				choiceValue: 'facilities1',
				choiceLabel: 'Facilities 1'
			});

			options.push({
				choiceValue: 'facilities2',
				choiceLabel: 'Facilities 2'
			});
			break;
		case 'legal':
			options.push({
				choiceValue: 'legal1',
				choiceLabel: 'Legal 1'
			});

			options.push({
				choiceValue: 'legal2',
				choiceLabel: 'Legal 2'
			});
			break;
		default:
			// stub
	}

	// add an Other option by default if a newValue is being set
	if ( newValue != '' ) {
		options.push({
			choiceValue: 'other',
			choiceLabel: 'Other'
		});
	}

	// add the derived options to the WhatNeeded choice list
	options.forEach( function(option) {
		g_form.addOption(fieldName, option.choiceValue, option.choiceLabel);
	});
}
