sys_script_include.script:
var NeedItUtils = Class.create();

NeedItUtils.prototype = {
    initialize: function() {
    },
	compareDateToToday: function(compareDate) {
		// rightnow stores the current time
		var today = new GlideDateTime().getLocalDate();
		// Create a GlideDateTime object for the When needed date
		var whenNeeded = new GlideDateTime(compareDate).getLocalDate();
		return today.compareTo(whenNeeded);
	},
    type: 'NeedItUtils'
};
